import Link from "next/link";

import React, { Component } from "react";
import { Helmet } from "react-helmet";
import { Carousel } from "react-responsive-carousel";
import "react-responsive-carousel/lib/styles/carousel.min.css";
import { YMaps, Map, Placemark, ZoomControl } from "react-yandex-maps";

import Menu from "../Menu/Menu";
import API from "../../Service/API";
import Footer from "../Menu/Footer";
import {
  NotificationContainer,
  NotificationManager
} from "react-notifications";
//import "react-notifications/lib/notifications.css";

import Comment from "./Comment";

class Item extends Component {
  constructor(props) {
    super(props);
    this.state = {
      data: {
        conditions: [],
        features: [],
        masters: [],
        imagesurl: "",
        map: ["43.235", "76.916", "12"]
      },
      items: []
    };
    this.handleBuyClick = this.handleBuyClick.bind(this);
  }
  componentDidMount() {
    window.scrollTo(0, 0);
    this.API = new API();
    this.API.getCompanyInfo(this.props.match.params.item).then(res => {
      this.setState({ data: res.data });
      this.API.getCompanyServices(this.props.match.params.item).then(ress => {
        if (ress.data.message === "Cannot catch items.")
          console.log("No Item :", ress.data.message);
        else this.setState({ items: ress.data.items });
      });
    });
  }

  render() {
    var ItemData = this.state.data;
    return (
      <div>
        <Helmet>
          <title>{ItemData.company}</title>
          <meta name="description" content={ItemData.description} />
        </Helmet>
        <Menu />

        <div className="itemcontainer">
          <div className="col-12 col-s-12">
            <div className="itemcard">
              <h1> {ItemData.company} </h1>
              <h2>
                <i className="descrip icon-tags"> </i>
                {ItemData.description}
              </h2>
              <div className="simplep">
                <p> {ItemData.address}</p>
              </div>
            </div>
          </div>
          {ItemData.imagesurl !== "" ? (
            <div className="col-7 col-s-7">
              <div className="carouselCard">
                <Carousel showIndicators={false}>
                  {ItemData.imagesurl.map(function(item) {
                    return (
                      <div key={item}>
                        <img src={item} alt={item} key={item} />
                      </div>
                    );
                  }, this)}
                </Carousel>
              </div>
            </div>
          ) : null}

          <div className="col-5-sticky">
            <div className="discountcard">
              <h3>Сертификаты на скидку</h3>
              <p className="icon-clock"> Осталось немного времени!</p>
              <div className="grouplist">
                <ul key={"discount"}>
                  {this.state.items.map(function(item, index) {
                    return (
                      <li key={this.generateKey(index)}>
                        <p className="discountP">{item.description}</p>
                        <div>
                          <span className="discount">
                            {Math.floor(
                              ((item.price - item.dprice) / item.price) * 100
                            )}
                            %
                          </span>
                          <span className="price">
                            {item.price} <span className="icon-tenge " />
                          </span>
                          <span className="dprice">
                            {item.dprice}
                            <i className="icon-tenge" />
                          </span>
                        </div>
                        <button
                          type="button"
                          className="discountbutton"
                          onClick={() => {
                            this.handleBuyClick(item);
                          }}
                        >
                          Купить
                        </button>
                      </li>
                    );
                  }, this)}
                </ul>
              </div>
            </div>
          </div>

          <div className="col-7 col-s-7">
            {ItemData.features[0] !== "" ? (
              <div className="itemcard">
                <h3>Описание и Особенности</h3>
                <div className="rounded-list">
                  {ItemData.features.map(i => (
                    <span key={i}>{i}</span>
                  ))}
                </div>
              </div>
            ) : null}

            {ItemData.conditions[0] !== "" ? (
              <div className="itemcard">
                <h3>Сроки и условия</h3>
                <div className="rounded-list-conditions">
                  {ItemData.conditions.map(i => (
                    <span key={i}>{i}</span>
                  ))}
                </div>
              </div>
