import React, { Component } from "react";
import Submenu from "./Submenu";
import { Link } from "react-router-dom";
class Menu extends Component {
  constructor(props) {
    super(props);
    this.state = {
      menuState: "All Deals",
      subMenuState: "Deal of Day",
      mobileMenu: true
    };
    this.handleSignout = this.handleSignout.bind(this);
    this.handleMenuClicked = this.handleMenuClicked.bind(this);
    this.handleSubMenuClicked = this.handleSubMenuClicked.bind(this);
    this.handleLink = this.handleLink.bind(this);
    this.onNavigate = this.onNavigate.bind(this);
  }

  componentDidMount() {
    if (document.documentElement.clientWidth < 500) {
      this.setState({ mobileMenu: false });
    }
  }

  onNavigate = (history, locationDescriptor) =>
    history.replace(locationDescriptor);

  render() {
    return (
      <div>
        <div className="toplogo">
          <Link to="/">
            <h1>понпон</h1>
          </Link>
          <input type="text" placeholder="поисковый ку пон" />
          <button className="searchButton">
            <i className="icon-search" />
          </button>

          <ul>
            <li
              key="iconitem"
              className="icon"
              onClick={() => {
                this.handleMenuClicked();
              }}
            >
              <i className="icon-bars" />
            </li>
            <li>
              {localStorage.getItem("jwt") ? (
                <div>
                  <Link
                    to="/"
                    key="signout"
                    name="signout"
                    onClick={this.handleSignout}
                  >
                    выйти <i className="icon-signout" />
                  </Link>

                  <Link to="/profile" key="profile" name="signout">
                    профиль
                  </Link>
                </div>
              ) : (
                <Link
                  to="/welcome"
                  onClick={event => {
                    this.props.history.replace("/welcome");
                    event.preventDefault();
                  }}
                >
                  Вход <i className="icon-signin" />
                </Link>
              )}
            </li>
          </ul>
        </div>

        <div
          className="topnav"
          style={{ display: this.state.mobileMenu ? "block" : "none" }}
        >
          <ul>
            <li>
              <Link
                to="/"
                onClick={() => {
                  this.props.handleChange("All Deals");
                }}
                onMouseEnter={() => {
                  this.setState({ menuState: "All Deals" });
                }}
                replace
              >
                Все
              </Link>
            </li>
            <li>
              <Link
                to="/Category/Beauty and Spas"
                onClick={() => {
                  this.props.handleChange("Beauty and Spas");
                }}
                onMouseEnter={() => {
                  this.setState({ menuState: "Beauty & Spas" });
                }}
                replace
              >
                красоты и спа
              </Link>
            </li>
            <li>
              <Link
                to="/Category/Health and Fitness"
                onClick={() => {
                  this.props.handleChange("Health and Fitness");
                }}
                onMouseEnter={() => {
                  this.setState({ menuState: "Health & Fitness" });
                }}
              >
                Здоровье и фитнес
              </Link>
            </li>
          </ul>
        </div>
        {this.state.mobileMenu ? (
          <Submenu
            item={this.state.menuState}
            subCategory={this.state.subMenuState}
            onSubMenuClick={subcategory => {
              this.handleSubMenuClicked(subcategory);
            }}
          />
        ) : null}
      </div>
    );
  }

  handleSignout() {
    localStorage.removeItem("jwt");
  }
  handleMenuClicked() {
    this.setState({ mobileMenu: !this.state.mobileMenu });
  }

  handleLink() {
    this.props.history.push("/welcome");
  }
  handleSubMenuClicked(subcategory) {
    this.setState({ subMenuState: subcategory });
    console.log("subcategory", subcategory);
    this.props.handleChange(subcategory);
  }
}

export default Menu;
