import React, { Component } from "react";
import Footer from "../components/menu/Footer.jsx";
import Menu from "../components/menu/Menu.jsx";
//import Cards from "../components/homepage/Cards.jsx";

import axios from "axios";

class Home extends Component {
  constructor(props) {
    super(props);
    this.state = {
      category: "All Deals",
      subcategory: "",
      link: ""
    };
  }

  static async getInitialProps() {
    const res = await axios.get("http://localhost/Backend/API/getallitems.php");
    // this.setState(res);
    console.log("test", res);
    return { data: res.data };
  }

  render() {
    return (
      <div>
        <Menu link={this.state.link} />
        aa{this.props.data.items[1].company}
        {/* <Cards
          items={this.state.items}
          category={this.state.category}
          subcategory={this.state.subcategory}
          link={this.state.link}
          history={this.props.history}
        /> */}
        <Footer />
      </div>
    );
  }

  handleChange = category => {
    let categories = [
      "All Deals",
      "Beauty and Spas",
      "Health and Fitness",
      "All Deals"
    ];
    if (categories.includes(category)) {
      this.setState({ category: category, subcategory: "" });
    } else {
      this.setState({ subcategory: category }, () => {
        console.log(
          "category:",
          this.state.category,
          "subcategory:",
          this.state.subcategory
        );
      });
    }
  };
}

export default Home;
