import React, { Component } from "react";
import Link from "next/link";

class Menu extends Component {
  constructor(props) {
    super(props);
    this.state = {
      menuState: "All Deals",
      subMenuState: "Deal of Day",
      mobileMenu: "true"
    };
  }
  componentDidMount() {
    if (document.documentElement.clientWidth < 500) {
      this.setState({ mobileMenu: "false" });
    }
  }
  render() {
    return (
      <div className="col-12">
        <div className="footer">{this.props.hel}</div>
        <div className="footer">{this.state.mobileMenu}</div>
      </div>
    );
  }
}

export default Menu;
