import React, { Component } from "react";
import Link from "next/link";

class Menu extends Component {
  constructor(props) {
    super(props);
    this.state = {
      menuState: "All Deals",
      subMenuState: "Deal of Day",
      mobileMenu: "true",
      logedin: false
    };
  }
  componentDidMount() {
    if (document.documentElement.clientWidth < 500) {
      this.setState({ mobileMenu: "false" });
    }
    if (localStorage.getItem("jwt")) this.setState({ logedin: true });
  }
  render() {
    return (
      <div>
        <div className="toplogo">
          <Link href="/">
            <a>
              <h1>понпон</h1>
            </a>
          </Link>
          <input type="text" placeholder="поисковый ку пон" />
          <button className="searchButton">
            <i className="icon-search" />
          </button>

          <ul>
            <li
              key="iconitem"
              className="icon"
              onClick={() => {
                this.handleMenuClicked();
              }}
            >
              <i className="icon-bars" />
            </li>
            <li>
              {this.state.logedin ? (
                <div>
                  <Link
                    to="/"
                    key="signout"
                    name="signout"
                    onClick={this.handleSignout}
                  >
                    <a>выйти</a> <i className="icon-signout" />
                  </Link>

                  <Link href="/" key="profile" name="signout">
                    <a>профиль</a>
                  </Link>
                </div>
              ) : (
                <Link
                  to="/"
                  onClick={event => {
                    this.props.history.replace("/welcome");
                    event.preventDefault();
                  }}
                >
                  <a>Вход</a> <i className="icon-signin" />
                </Link>
              )}
            </li>
          </ul>
        </div>

        <div
          className="topnav"
          style={{ display: this.state.mobileMenu ? "block" : "none" }}
        >
          <ul>
            <li>
              <Link
                to="/"
                onClick={() => {
                  this.props.handleChange("All Deals");
                }}
                onMouseEnter={() => {
                  this.setState({ menuState: "All Deals" });
                }}
                replace
              >
                Все
              </Link>
            </li>
            <li>
              <Link
                to="/Category/Beauty and Spas"
                onClick={() => {
                  this.props.handleChange("Beauty and Spas");
                }}
                onMouseEnter={() => {
                  this.setState({ menuState: "Beauty & Spas" });
                }}
                replace
              >
                красоты и спа
              </Link>
            </li>
            <li>
              <Link
                to="/Category/Health and Fitness"
                onClick={() => {
                  this.props.handleChange("Health and Fitness");
                }}
                onMouseEnter={() => {
                  this.setState({ menuState: "Health & Fitness" });
                }}
              >
                Здоровье и фитнес
              </Link>
            </li>
          </ul>
        </div>
      </div>
    );
  }
}

export default Menu;
